/* ------------------------------------------------------- */

/* Original: https://github.com/Roboron3042/Cyberpunk-Neon */

html {
  /* ??? */
  /* toolbar / frame / popup / sidebar */
  --background-text: #ff000f;
  /* button_background_hover */
  --button-alt-svg: invert(20%) sepia(10%) saturate(50%) hue-rotate(180deg) brightness(80%) contrast(90%);

  /* Main BG */
  --background: #313744;

  /* Primary buttons */
  --button: #404757;

  /* toolbar_field */
  --button-alt: #313641;

  /* Text */
  --text: #90adad;

  /* popup_text / tab_background_text */
  --text-svg: invert(55%) sepia(15%) saturate(90%) hue-rotate(150deg) brightness(95%) contrast(90%);
  --text-alt: #798282;

  /* Borders & accents */
  --border: #576c6b;
  --accent: #91aaad;
  --accent-svg: invert(60%) sepia(10%) saturate(60%) hue-rotate(160deg) brightness(100%) contrast(90%);

  /* Selections & highlights */
  --select: #616d6d;
  --select-alt: #4a545f;

  /* Greens & warnings */
  --green: #96bfc2;
}


/* 1. Apply into Discord’s .theme-dark */
.theme-dark {
  --activity-card-background: var(--background) !important;
  --app-border-frame: var(--border) !important;
  --background-accent: var(--select-alt) !important;
  --background-base-lowest: var(--background) !important;
  --background-base-lower: var(--background) !important;
  --background-base-low: var(--background) !important;
  --background-floating: var(--button-alt) !important;
  --background-nested-floating: var(--background) !important;
  --background-mentioned-hover: var(--select-alt) !important;
  --background-mentioned: rgba(63, 74, 84, 1.0) !important;
  --background-message-hover: var(--button-alt) !important;
  --background-mobile-primary: var(--button-alt) !important;
  --background-mobile-secondary: var(--background) !important;
  --background-mod-normal: var(--button-alt) !important;
  --background-mod-subtle: var(--button) !important;
  --background-modifier: var(--button) !important;
  --background-modifier-accent: #313744 !important;
  --background-modifier-active: #4b545f !important;
  --background-modifier-hover: var(--button-alt) !important;
  --background-modifier-selected: #4b545f !important;
  --background-primary: var(--background) !important;
  --background-secondary-alt: var(--background) !important;
  --background-secondary: var(--background) !important;
  --background-surface-high: var(--button) !important;
  --background-surface-higher: var(--button-alt) !important;
  --background-surface-highest: var(--select) !important;
  --background-tertiary: var(--background) !important;
  --bg-brand: var(--select-alt) !important;
  --bg-base-tertiary: var(--background) !important;
  --bg-mod-subtle: var(--button) !important;
  --bg-surface-raised: var(--button-alt) !important;
  --bg-mod-strong: var(--button-alt) !important;
  --bg-overlay-app-frame: var(--background) !important;
  --border-subtle: var(--border) !important;
  --button-secondary-text: var(--text) !important;
  --button-secondary-background: var(--button-alt) !important;
  --button-secondary-background-hover: var(--select-alt) !important;
  --button-filled-brand-background: var(--button) !important;
  --button-filled-brand-background-hover: var(--select-alt) !important;
  --button-outline-danger-text: #ec797b !important;
  --button-outline-danger-text-hover: #ec797b !important;
  --button-outline-danger-background: var(--button) !important;
  --button-outline-danger-background-hover: var(--select-alt) !important;
  --button-danger-background: var(--button) !important;
  --button-danger-background-hover: var(--select-alt) !important;
  --brand-experiment: var(--button) !important;
  --brand-experiment-30a: transparent !important;
  --brand-experiment-200: var(--green) !important;
  --brand-experiment-360: var(--text) !important;
  --brand-experiment-500: var(--select-alt) !important;
  --brand-360: var(--accent) !important;
  --brand-500: var(--select-alt) !important;
  --channel-icon: var(--text) !important;
  --channels-default: rgba(126, 151, 151, 0.8) !important;
  --channeltextarea-background: var(--button) !important;
  --chat-background-default: var(--button-alt) !important;
  --chat-text-muted: var(--text-alt) !important;
  --checkbox-background-checked: var(--select) !important;
  --checkbox-border-default: var(--border) !important;
  --control-brand-foreground-new: var(--accent) !important;
  --custom-channel-members-bg: var(--background) !important;
  --deprecated-card-bg: rgba(49, 55, 68, 0.6) !important;
  --deprecated-card-editable-bg: rgba(64, 71, 87, 0.2) !important;
  --deprecated-panel-background: var(--button-alt) !important;
  --deprecated-quickswitcher-input-background: var(--button) !important;
  --deprecated-quickswitcher-input-placeholder: rgba(126, 151, 151, 0.3) !important;
  --deprecated-store-bg: var(--button-alt) !important;
  --deprecated-text-input-bg: var(--button-alt) !important;
  --deprecated-text-input-border-disabled: var(--background) !important;
  --deprecated-text-input-border-hover: var(--accent) !important;
  --deprecated-text-input-border: var(--text) !important;
  --deprecated-text-input-prefix: var(--text) !important;
  --divider-color: var(--accent) !important;
  --divider-subtle: var(--border) !important;
  --elevation-high: 0 8px 16px rgba(0, 0, 0, 0.24) !important;
  --elevation-low: 0 1px 0 rgba(4, 4, 5, 0.2), 
                 0 1.5px 0 rgba(6, 6, 7, 0.05), 
                 0 2px 0 rgba(4, 4, 5, 0.05) !important;
  --guild-header-text-shadow: 0 1px 1px rgba(0, 0, 0, 0.4) !important;
  --header-primary: var(--accent) !important;
  --header-secondary: var(--text) !important;
  --icon-primary: var(--text) !important;
  --icon-secondary: var(--accent) !important;
  --icon-tertiary: var(--text-alt) !important;
  --info-warning-foreground: var(--accent) !important;
  --input-border: var(--border) !important;
  --interactive-active: var(--accent) !important;
  --interactive-hover: var(--accent) !important;
  --interactive-muted: rgba(126, 151, 151, 0.6) !important;
  --interactive-normal: var(--text) !important;
  --logo-primary: var(--accent) !important;
  --scrollbar-auto-scrollbar-color-thumb: var(--text) !important;
  --scrollbar-auto-scrollbar-color-track: var(--background) !important;
  --spoiler-revealed-background: var(--green) !important;
  --spoiler-hidden-background: var(--green) !important;
  --text-default: var(--accent) !important;
  --text-positive: var(--green) !important;
  --text-warning: var(--accent) !important;
  --text-link: var(--accent) !important;
  --text-muted: rgba(126, 151, 151, 0.6) !important;
  --text-normal: var(--text) !important;
  --text-primary: var(--text) !important;
  --text-secondary: var(--text-alt) !important;
  --textbox-markdown-syntax: var(--accent) !important;
  --user-profile-overlay-background: var(--background) !important;
}


/* 1. Miscellaneous overrides */

/* 1.1 Windows title-bar buttons */
.winButton-iRh8-Z:hover {
  background-color: var(--button-alt) !important;
}
.appMount-3lHmkl {
  background-color: var(--background) !important;
}

/* 1.2 macOS title-bar buttons */
.typeMacOS-3EmCyP {
  background-color: var(--background) !important;
}
.macButtonClose-MwZ2nf {
  background-color: #ec797b !important;
}
.macButtonMinimize-3EJ6wo {
  background-color: #e5888a !important;
}
.macButtonMaximize-2FMHAO {
  background-color: var(--green) !important;
}

/* 1.3 Text selection */
::selection,
.clipboard-input-inner input::selection,
.CodeMirror .CodeMirror-selected,
.CodeMirror-line::selection,
.CodeMirror-line > span::selection,
.CodeMirror-line > span > span::selection,
.note textarea::selection {
  color: var(--text) !important;
  background: var(--select) !important;
}

/* 1.4 Scroll bar */
.da-scrollerWrap .da-scroller::-webkit-scrollbar-thumb {
  background-color: var(--text) !important;
  border-color: var(--background) !important;
}
.theme-dark .da-scrollerWrap .da-scroller::-webkit-scrollbar-track-piece {
  background-color: var(--background) !important;
  border-color: var(--background) !important;
}

/* 1.5 Placeholder text */
::-webkit-input-placeholder {
  color: var(--text-alt) !important;
}
textarea::placeholder {
  color: var(--text-alt) !important;
}

/* Divider override */
.divider__908e2 {
  --divider-color: var(--accent) !important;
}


/* ------------------------------------------------------ */


/* Discord Minimize Sidebars (https://userstyles.world/api/style/4877.user.css) – Personal Settings */

/* –– CHANNEL SIDEBAR (minimize disabled) –– */
[class*="sidebarList_"] {
    transition: width 0.35s, opacity 0.35s;
    opacity: 1;
}

/* –– GUILD (SERVER) SIDEBAR –– */
[class*="guilds_"] {
    transition: width 0.15s ease, opacity 0.25s ease;
    opacity: 1;
}

body:not(:has(#guild-context)) 
  [class*='app_'] [class*='base_'] [class*="sidebar_"]:not(:hover):not(:focus-within) 
  [class*="guilds_"]:not(:hover):not(:focus-within) {
    transition: width 0.55s ease, opacity 0.45s ease;
    width: 1px;
    opacity: 0.00;
}

/* –– TOP BAR –– */
[class*="bar_"]:has([class*="title_"] + [class*="leading_"] + [class*="trailing_"]) {
    transition: height 0.35s, opacity 0.35s;
    opacity: 1;
}

[class*="bar_"]:has([class*="title_"] + [class*="leading_"] + [class*="trailing_"]):not(:hover):not(:focus-within) {
    transition: height 0.35s 0.30s, opacity 0.35s 0.30s;
    opacity: 0.00;
}

[class*="base_"]:has([class*="bar_"] [class*="title_"] + [class*="leading_"] + [class*="trailing_"]) {
    transition: grid-template-rows 0.35s;
}

[class*="base_"]:has(
    [class*="bar_"]:not(:hover):not(:focus-within) 
    [class*="title_"] + [class*="leading_"] + [class*="trailing_"]
) {
    transition: grid-template-rows 0.35s 0.30s;
    --custom-app-top-bar-height: 0px;
}

[class*="base_"]:has(
    [class*="bar_"]:not(:hover):not(:focus-within) 
    [class*="title_"] + [class*="leading_"] + [class*="trailing_"]
) * {
    --custom-app-top-bar-height: 0px;
}

/* –– USER PANEL FADE –– */
[class*='app_'] [class*='base_'] [class*="sidebar_"] [class*="panels_"] {
    transition: opacity 0.35s;
}

body:not(:has(#channel-context, #guild-context, [class*="accountProfilePopoutWrapper_"])) 
  [class*='app_'] [class*='base_'] [class*="sidebar_"]:not(:hover):not(:focus-within) 
  [class*="panels_"]:not(:hover):not(:focus-within) {
    transition: opacity 0.35s 0.30s;
    opacity: 0.00;
}

/* –– USER PANEL SQUEEZED STYLE –– */
[class*='app_'] [class*='base_'] [class*="sidebar_"] [class*="panels_"] {
    overflow: hidden;
}
[class*='app_'] [class*='base_'] [class*="sidebar_"] [class*="panels_"] [class*="avatarWrapper_"] {
    z-index: 1;
}

/* –– USER PANEL FLOATING BALL –– */
[class*='app_'] [class*='base_'] [class*="sidebar_"] [class*="panels_"] {
    position: absolute;
    width: 220px;
    left: 77px;
    transition: width 0.35s, left 0.35s;
}

body:not(:has([class*="accountProfilePopoutWrapper_"])) 
  [class*='app_'] [class*='base_'] [class*="sidebar_"] 
  [class*="panels_"]:not(:hover):not(:focus-within) {
    left: 77px;
    width: 9px;
    transition: width 0.35s 0.30s, left 0.35s 0.30s;
}

[class*='app_'] [class*='base_'] [class*="sidebar_"] [class*="sidebarList_"] > * {
    padding-bottom: 0;
}
[class*='app_'] [class*='base_'] [class*="sidebar_"] [class*="guilds_"] {
    margin-bottom: 0;
}

/* –– FIREFOX FIXES –– */
[class*='app_'] [class*='base_'] [class*="sidebar_"] {
    width: unset !important;
    overflow: visible;
}

